import React from 'react';
import { TextField, Grid, Typography, FormControl, InputLabel, Input, Paper} from '@material-ui/core';
import makeStyles from '@material-ui/core/styles/makeStyles';

const useStyles = makeStyles(theme => ({
    formRow: {
        '& .MuiTextField-root': {
        },
    },
    paper: {
        marginTop: theme.spacing(2),
        padding: theme.spacing(4)
    },
    formTitle: {
        marginBottom: theme.spacing(1)
    }
}));

function Form() {
        const classes = useStyles();

        return (
            <Paper elevation={3} className={classes.paper}>
                <Typography variant="subtitle1" className={classes.formTitle}>Enter your information below:</Typography>
                <Grid container component="form" spacing={3}>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={6}>
                                <TextField id="fname" label="First Name" fullWidth variant="outlined"/>
                            </Grid>
                            <Grid item sm={6}>
                                <TextField id="lname" label="Last Name" fullWidth variant="outlined"/>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={12}>
                                <TextField id="email" label="Email" fullWidth variant="outlined"/>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={12} component="FormControl">
                                <TextField id="password" label="Password" type="password" fullWidth variant="outlined"/>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
        );
    }


export default Form;
