import React from 'react';
import { TextField, Grid, FormControl, InputLabel, Input, Paper} from '@material-ui/core';
import makeStyles from '@material-ui/core/styles/makeStyles';

const useStyles = makeStyles(theme => ({
    formRow: {
        '& .MuiTextField-root': {
        },
    },
    paper: {
        marginTop: theme.spacing(5)
    }
}));

function Form() {
        const classes = useStyles();

        return (
            <Paper elevation={3} className={classes.paper}>
                <Grid container component="form" spacing={3}>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={3}>
                                <TextField id="lname" label="Last Name" fullWidth variant="standard"/>
                            </Grid>
                            <Grid item sm={3}>
                                <TextField id="fname" label="First Name" fullWidth variant="standard"/>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={6}>
                                <TextField id="email" label="Email" fullWidth variant="standard"/>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.formRow}>
                        <Grid container spacing={3} justify="flex-start">
                            <Grid item sm={6} component="FormControl">
                                <TextField id="password" label="Password" type="password" fullWidth variant="standard"/>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
        );
    }


export default Form;
