import React from 'react';
import { TextField, Grid } from '@material-ui/core';
import makeStyles from '@material-ui/core/styles/makeStyles';

const useStyles = makeStyles(theme => ({
    formRow: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
        }
    }
}));

function Form() {
        const classes = useStyles();

        return (
            <Grid container spacing={3}>
                <Grid item xs={12} className={classes.formRow}>
                    <Grid container justify="space-between">
                        <Grid item>
                            <TextField id="fname" label="First Name" variant="standard"/>
                            <TextField id="lname" label="Last Name" variant="standard"/>

                        </Grid>
                        </Grid>
                </Grid>
                <Grid item xs={12} className={classes.formRow} sm={6}>
                    <TextField id="email" label="Email" fullWidth variant="standard"/>
                </Grid>
            </Grid>
        );
    }


export default Form;
