import React from 'react';
import {Grid, Button, Paper, Typography} from '@material-ui/core';
import ReactDOM from 'react-dom';
import AccountForm from './signUp';
import {withStyles} from '@material-ui/core/styles';

export default class FormPaper extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pageNum: 1
        }
    }

    handleButtonClick(val) {
        this.setState({pageNum: this.state.pageNum + val})
    }

    render() {
        return (
            <Paper elevation={3} style={{marginTop: '16px', padding:'32px'}}>
                <Typography variant="h6" style={{marginBotton: '24px'}}>Enter your information:</Typography>
                <AccountForm/>
                <Grid container spacing={2}>
                    <Grid item xs={12}>
                        <Grid container spacing={2} alignItems="flex-end">
                            <Grid item>
                                <Button color="default" variant="contained">Back</Button>
                            </Grid>
                            <Grid item>
                                <Button color="primary" variant="contained">Next</Button>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
        );
    }
}
