import React from 'react';
import {Grid, Button, Paper} from '@material-ui/core';
import ReactDOM from 'react-dom';
import AccountForm from './signUp';
import makeStyles from '@material-ui/core/styles/makeStyles';


const classes = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(2),
        padding: theme.spacing(4)
    },
}));

export default class FormPaper extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pageNum: 1
        }
    }

    handleButtonClick(val) {
        this.setState({pageNum: this.state.pageNum + val})
    }

    render() {
        return (
            <Paper elevation={3} style={{marginTop: theme.spacing(2), padding: theme.spacing(4)}}>
                <AccountForm/>
                <Grid container spacing={2}>
                    <Grid item xs={12}>
                        <Grid container spacing={2} alignItems="flex-end">
                            <Grid item>
                                <Button color="default" variant="contained">Back</Button>
                            </Grid>
                            <Grid item>
                                <Button color="primary" variant="contained">Next</Button>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
        );
    }
}
