import React from 'react';
import {Grid, Button, Paper, Typography} from '@material-ui/core';
import AccountForm from './signUp';

export default class FormPaper extends React.Component {    
    constructor(props) {
        super(props);
        this.state = {
            pageNum: 1
        }
    }

    static headers = ['Enter your information: '];

    handleButtonClick(val) {
        this.setState({pageNum: this.state.pageNum + val})
    }

    render() {
        console.log(headers);
        return (
            <Paper elevation={3} style={{marginTop: '16px', padding:'32px'}}>
                <Typography variant="h6" style={{marginBottom: '24px',}}>{this.headers[this.state.pageNum-1]}</Typography>
                {(() => {
                    switch (this.state.pageNum) {
                        case 1:
                            return <AccountForm/>;
                        default:
                            return null;
                    }
                })()}
                <Grid container spacing={2}>
                    <Grid item xs={12}>
                        <Grid container style={{marginTop: '24px'}} spacing={1} justify="flex-end">
                            <Grid item>
                                <Button color="default" variant="contained" disabled={this.state.pageNum === 1 ? 'true':'false'}>Back</Button>
                            </Grid>
                            <Grid item>
                                <Button color="primary" onClick={() => {this.handleButtonClick(1)}} variant="contained">Next</Button>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>
        );
    }
}
