import React from 'react';
import { TextField, Grid, FormControl, InputLabel, Input } from '@material-ui/core';
import makeStyles from '@material-ui/core/styles/makeStyles';

const useStyles = makeStyles(theme => ({
    formRow: {
        '& .MuiTextField-root': {
        },
    }
}));

function Form() {
        const classes = useStyles();

        return (
            <Grid container component="form" spacing={3}>
                <Grid item xs={12} className={classes.formRow}>
                    <Grid container spacing={3} justify="flex-start">
                        <Grid item sm={3}>
                            <TextField id="lname" label="Last Name" fullWidth variant="standard"/>
                        </Grid>
                        <Grid item sm={3}>
                            <TextField id="fname" label="First Name" fullWidth variant="standard"/>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid item xs={12} className={classes.formRow}>
                    <Grid container spacing={3} justify="flex-start">
                        <Grid item sm={6}>
                            <TextField id="email" label="Email" fullWidth variant="standard"/>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid item xs={12} className={classes.formRow}>
                    <Grid container spacing={3} justify="flex-start">
                        <Grid item sm={6}>
                            <FormControl>
                                <InputLabel htmlFor="password">Password</InputLabel>
                                <Input 
                                    id="password"
                                />
                            </FormControl>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        );
    }


export default Form;
